  {
    id: '1',
    name: 'Game',
  },
  {
    id: '2',
    name: 'Rating'
  },
  {
    id: '3',
    name: 'Exit'
  }
]

const nav = (item) => {
  if (item === 'Game') {
    item = 1
  } else if (item === 'Rating') {
    item = 2
  } else {
    item = 3
  }
  return item
}

