import React, { useState } from 'react';
import { StyleSheet, View, Button, TouchableOpacity, Text } from 'react-native';
import MarginView from '../../components/Common/MarginView';

import firebase from '../../components/firebase';

export const Game = ({ navigation }) => {

    const [data, setData] = useState({
        levelOne: false,
        levelTwo: false,
        levelThree: false,
        levelFour: false,
        levelFive: false,
        levelSix: false,
        levelSeven: false,
        levelEight: false,
        levelNine: false,
        levelTen: false,
    })

    const [j, setJ] = useState(false)
    const getLevels = () => {
        firebase.db.collection('score').doc('PB959oJmX6IAKuYxUzGB').get().then(function (doc) {
            if (doc.exists) {
                // console.log("Document data:", doc.data());
                setData({ levelOne: doc.get('levelOne') });
                setData({ levelTwo: doc.get('levelTwo') });
                setData({ levelThree: doc.get('levelThree') });
                setData({ levelFour: doc.get('levelFour') });
                setData({ levelFive: doc.get('levelFive') });
                setData({ levelSix: doc.get('levelSix') });
                setData({ levelSeven: doc.get('levelSeven') });
                setData({ levelEight: doc.get('levelEight') });
                setData({ levelNine: doc.get('levelNine') });
                setData({ levelTen: doc.get('levelTen') });
                setJ(doc.get('levelOne'))
            } else {
                console.log("No such document!");
            }
        }).catch(function (error) {
            console.log("Error getting document:", error);
        });
    }

    return (
        <View style={styles.container}>
            <View style={styles.form}>
                {getLevels()}
                <View style={{ flexDirection: 'row' }}>
                    <View style={styles.butt}>
                        <Button
                            color="#424874"
                            backgroundColor='#424874'
                            onPress={() => navigation.navigate('LevelOne')} title='Level 1' />
                    </View>
                    {data.levelOne === true ? <TouchableOpacity style={styles.done}><Text>Done</Text></TouchableOpacity> : null}
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button
                        color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelTwo')} title='Level 2' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button
                        color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelThree')} title='Level 3' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelFour')} title='Level 4' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelFive')} title='Level 5' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelSix')} title='Level 6' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelSeven')} title='Level 7' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button
                        color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelEight')} title='Level 8' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button
                        color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelNine')} title='Level 9' />
                </View>
                <MarginView margin={10} />
                <View style={styles.butt}>
                    <Button color="#424874"
                        backgroundColor='#424874'
                        onPress={() => navigation.navigate('LevelTen')} title='Level 10' />
                </View>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor: '#a6b1e1',
        flexDirection: 'column'
    },
    done: {
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: '#424874',
        padding: 4,
    },
    butt: {
        borderStyle: 'solid',
        borderWidth: 2,
        borderColor: '#424874',
        flexDirection: 'row'
    },
    form: {
        backgroundColor: '#dcd6f7',
        padding: 30,
        minWidth: 200,
        marginTop: 20,
        marginBottom: 20
    }
});
