import React, { useState } from 'react';
import { StyleSheet, View, Button } from 'react-native';
import { Ionicons, FontAwesome, Entypo } from '@expo/vector-icons'; // 6.2.2

import Header from '../components/Header';
import Score from '../components/Score';
import Card from '../components/GameCard';

import helpers from './helpers';

const LevelTenth = props => {

    let sources = {
        'fontawesome': FontAwesome,
        'entypo': Entypo,
        'ionicons': Ionicons
    };

    let cards = [
        {
            src: 'fontawesome',
            name: 'heart',
            color: 'red'
        },
        {
            src: 'entypo',
            name: 'feather',
            color: '#7d4b12'
        },
        {
            src: 'entypo',
            name: 'flashlight',
            color: '#f7911f'
        },
        {
            src: 'entypo',
            name: 'flower',
            color: '#37b24d'
        },
        {
            src: 'entypo',
            name: 'moon',
            color: '#ffd43b'
        },
        {
            src: 'entypo',
            name: 'youtube',
            color: '#FF0000'
        },
        {
            src: 'entypo',
            name: 'shop',
            color: '#5f5f5f'
        },
        {
            src: 'fontawesome',
            name: 'github',
            color: '#24292e'
        },
        {
            src: 'fontawesome',
            name: 'skype',
            color: '#1686D9'
        },
        {
            src: 'fontawesome',
            name: 'send',
            color: '#1c7cd6'
        },
        {
            src: 'ionicons',
            name: 'ios-magnet',
            color: '#d61c1c'
        },
        {
            src: 'ionicons',
            name: 'logo-facebook',
            color: '#3C5B9B'
        }

    ];

    let clone = JSON.parse(JSON.stringify(cards));

    cards = cards.concat(clone);
    cards.map((obj) => {
        let id = Math.random().toString(36).substring(7);
        obj.id = id;
        obj.src = sources[obj.src];
        obj.is_open = false;
    });

    cards = cards.shuffle();
    const [data, setData] = useState({
        current_selection: [],
        selected_pairs: [],
        score: 0,
        cards: cards
    })

    const resetCards = () => {
        let cards = cards.map((obj) => {
            obj.is_open = false;
            return obj;
        });

        cards = cards.shuffle();

        setData({
            current_selection: [],
            selected_pairs: [],
            cards: cards,
            score: 0
        });
    }


    const renderRows = () => {

        let contents = getRowContents(data.cards);
        return contents.map((cards, index) => {
            return (
                <View key={index} style={styles.row}>
                    { renderCards(cards)}
                </View>
            );
        });

    }


    const renderCards = (cards) => {
        return cards.map((card, index) => {
            return (
                <Card
                    key={index}
                    src={card.src}
                    name={card.name}
                    color={card.color}
                    is_open={card.is_open}
                    clickCard={clickCard(card.id)}
                />
            );
        });
    }


    const clickCard = (id) => {
        let selected_pairs = data.selected_pairs;
        let current_selection = data.current_selection;
        let score = data.score;

        let index = data.cards.findIndex((card) => {
            return card.id == id;
        });

        let cards = data.cards;

        if (cards[index].is_open == false && selected_pairs.indexOf(cards[index].name) === -1) {

            cards[index].is_open = true;

            current_selection.push({
                index: index,
                name: cards[index].name
            });

            if (current_selection.length == 2) {
                if (current_selection[0].name == current_selection[1].name) {
                    score += 1;
                    selected_pairs.push(cards[index].name);
                } else {

                    cards[current_selection[0].index].is_open = false;

                    setTimeout(() => {
                        cards[index].is_open = false;
                        setData({
                            cards: cards
                        });
                    }, 500);
                }

                current_selection = [];
            }

            setData({
                score: score,
                cards: cards,
                current_selection: current_selection
            });

        }

    }


    const getRowContents = (cards) => {
        let contents_r = [];
        let contents = [];
        let count = 0;
        cards.forEach((item) => {
            count += 1;
            contents.push(item);
            if (count == 5) {
                contents_r.push(contents)
                count = 0;
                contents = [];
            }
        });

        return contents_r;
    }

    return (
        <View style={styles.container}>
            <Header />
            <View style={styles.body}>
                {
                    renderRows()
                }
            </View>
            <Score score={data.score} />
            <Button
                onPress={resetCards()}
                title="Reset"
                color="#008CFA"
            />
        </View>
    );

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignSelf: 'stretch',
        backgroundColor: '#fff'
    },
    row: {
        flex: 1,
        flexDirection: 'row'
    },
    body: {
        flex: 18,
        justifyContent: 'space-between',
        padding: 10,
        marginTop: 20
    }
});

export default LevelTenth
