import React, { Component } from 'react';
import { Button, Text, View, StyleSheet, FlatList, TouchableOpacity } from "react-native";
import MarginView from '../Common/MarginView';

var horizontalFlatListData = [
  {
    id: 1,
    name: 'Game',
  },
  {
    id: 2,
    name: 'Rating'
  },
  {
    id: 3,
    name: 'Exit'
  }
]


const HorizontalFlatListItem = (props, { navigation }) => {
  const { item } = props
  const nav = (item) => {
    if (item === 'Game') {
      item = 1
    } else if (item === 'Rating') {
      item = 2
    } else {
      item = 3
    }
    return item
  }
  // render() {
  return <View style={styles.form}>{
    item = 1 && navigation.navigate('Game')
  }
    <TouchableOpacity onPress={(item) => {
      item == "Game"
      return nav(item)
    }}><Text>{item.name}</Text></TouchableOpacity>
    {/* <Button
        color="#00b241"
        title="Game"
        onPress={() => this.props.navigation.navigate('Game')}
      /> */}
    {/* <MarginView margin={15} />
      <Button
        color="#4286f5"
        title="Rating"
        onPress={() => this.props.navigation.navigate('RatingPage')}
      />
      <MarginView margin={15} />
      <Button
        color="#e34b34"
        title="Exit"
        onPress={() => this.props.navigation.navigate('Login')}
      /> */}
  </View>
  // }
}

export default class MainMenu extends Component {
  render() {
    return (
      <View style={styles.container}>
        <View>
          {/* <ScrollView horizontal={true}> */}

          <FlatList
            horizontal={true}
            data={horizontalFlatListData}
            renderItem={({ item, index }) => {
              return <HorizontalFlatListItem item={item} index={index} ></HorizontalFlatListItem>
            }}
            keyExtractor={(item, index) => item.id}
          />
          {/* </ScrollView> */}
        </View>
      </View>
    )
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#a6b1e1'
  },
  form: {
    backgroundColor: '#f4eeff',
    padding: 30,
    minWidth: 200,
    flexDirection: 'row',
    marginRight: 15
  }
});
